import React, { useRef, useContext, useState, useCallback } from 'react'

import { Box, Flex, Text, Heading, Button, Input, ButtonGroup } from '@chakra-ui/react'
import { Timer } from '../shared/Timer'
import { Heading2XL, HeadingLg, HeadingXL, TextXL } from '@components/shared/Typography'
import { useRouter } from 'next/router'
import QuestWrapper from '../shared/QuestWrapper'

const LANDING = 0
const BURNING = 1
const BURN_SUCCESS = 2
const BEGIN_QUEST = 3
const CLAIM_ART = 4
const CLAIMED_ART = 5

const BurnBoard = () => {
  const router = useRouter()
  const [view, viewSet] = useState(LANDING)

  const onBurn = useCallback(() => {
    viewSet(BURN_SUCCESS)
  }, [])

  const onBeginQuest = useCallback(() => {
    viewSet(BEGIN_QUEST)
  }, [])

  const onSubmitQuest = useCallback(() => {
    viewSet(CLAIM_ART)
  }, [])

  const onClaimArt = useCallback(() => {
    viewSet(CLAIMED_ART)
  }, [])
  return (
    <Box
      flex="1"
      position={'relative'}
      display="flex"
      w="100%"
      h="100%"
      flexDirection={'column'}
      justifyContent={'center'}
      alignItems="center"
      __css={{
        background: `${
          view !== LANDING
            ? 'linear-gradient(180deg, rgba(0, 0, 0, 0.8) 0%, rgba(0, 0, 0, 0) 91.67%), linear-gradient(0deg, rgba(0, 0, 0, 0.4), rgba(0, 0, 0, 0.4)), url(/img/user/ai-burn.png)'
            : ' '
        }`,
      }}
      backgroundPosition={'center'}
      backgroundSize={'cover'}
      backgroundRepeat="no-repeat"
    >
      <Flex
        w={'container.lg'}
        maxW="container.lg"
        h="100%"
        direction="column"
        gap="48px"
        justifyContent={'center'}
        alignItems="center"
      >
        {view === LANDING && (
          <>
            <Heading2XL>The final adventure awaits.</Heading2XL>

            <Text fontSize={'2xl'} textAlign="center" lineHeight={'36px'}>
              Burn your tokens in exchange for an NFT created by your decisions. Be thoughtful, the
              choices you make cannot be undone.
            </Text>
            <Button
              w={{ base: '192px', md: '296px' }}
              onClick={() => viewSet(BURNING)}
              variant="orange"
            >
              BEGIN
            </Button>
          </>
        )}
        {view === BURNING && (
          <>
            <Flex direction="column" textAlign="center" gap="24px">
              <Heading2XL>What will your burn?</Heading2XL>
              <Text fontSize={'2xl'} textAlign="center" p="16px">
                Once you choose to burn, your tokens will be burned from your wallet and gone
                forever.
              </Text>
            </Flex>

            <Flex gap="3rem">
              <Flex flex="50%">
                <PlaceholderTrait />
              </Flex>
              <Flex flex="50%">
                <BurnFrame onBurn={onBurn} />
              </Flex>
            </Flex>
          </>
        )}

        {view === BURN_SUCCESS && (
          <QuestWrapper>
            <HeadingXL>You have burned 10 tokens</HeadingXL>
            <TextXL>Start your quest to make the final NFT art</TextXL>
            <Button w={{ base: '192px', md: '296px' }} onClick={onBeginQuest} variant="orange">
              BEGIN QUEST
            </Button>
          </QuestWrapper>
        )}

        {view === BEGIN_QUEST && (
          <QuestWrapper>
            <BurnQuest onSubmitQuest={onSubmitQuest} />
          </QuestWrapper>
        )}

        {view === CLAIM_ART && (
          <QuestWrapper>
            <HeadingXL>Your Journey is Complete</HeadingXL>
            <TextXL>
              Claim your unrevealed artwork below. The art will be a result of your interaction with
              the AI and what you have burned.
            </TextXL>
            <Button w={{ base: '192px', md: '240px' }} onClick={onClaimArt} variant="orange">
              CLAIM ART
            </Button>
          </QuestWrapper>
        )}

        {view === CLAIMED_ART && (
          <QuestWrapper>
            <HeadingXL>Your art has been claimed</HeadingXL>
            <TextXL>
              You may take the quest again for another art piece if you have remaining tokens.
              Return during the Forge to gain Permanence.
            </TextXL>
            <ButtonGroup gap="3rem">
              <Button
                w={{ base: '192px', md: '240px' }}
                onClick={() => console.log('share on twiter')}
                variant="outline-orange"
              >
                SHARE ON TWITTER
              </Button>
              <Button
                w={{ base: '192px', md: '240px' }}
                onClick={() => console.log('view may token')}
                variant="orange"
              >
                VIEW ON OPENSEA
              </Button>
            </ButtonGroup>
          </QuestWrapper>
        )}
      </Flex>
      <Timer />
    </Box>
  )
}

export default BurnBoard

const BurnFrame = ({ onBurn }) => {
  return (
    <Box position="relative">
      <BurnFrameSvg />
      <Flex
        position="absolute"
        direction="column"
        top="0"
        left="0"
        justifyContent={'center'}
        alignItems="center"
        w="100%"
        h="100%"
        gap="24px"
      >
        <Heading size="lg" color="orange.400">
          Select quantity
        </Heading>
        <Flex gap="48px">
          <Flex direction="column" gap="1rem">
            <Flex direction="column" position="relative" className="token-quantity">
              <svg
                width="100%"
                height="100%"
                viewBox="0 0 296 56"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <rect width="296" height="56" rx="12" fill="black" />
                <rect
                  x="0.5"
                  y="0.5"
                  width="295"
                  height="55"
                  rx="11.5"
                  stroke="white"
                  strokeOpacity="0.16"
                />
              </svg>
              <Flex
                position="absolute"
                top="0"
                left="0"
                justifyContent={'space-between'}
                alignItems="center"
                w="100%"
                h="100%"
              >
                <Flex w="43px" justifyContent={'center'} alignItems="center">
                  <Text fontSize={'3xl'} cursor="pointer">
                    -
                  </Text>
                </Flex>

                <Text fontSize={'xl'} color="orange.400">
                  10
                </Text>
                <Flex w="43px" justifyContent={'center'} alignItems="center">
                  <Text fontSize={'3xl'} cursor="pointer">
                    +
                  </Text>
                </Flex>
              </Flex>
            </Flex>
            <Flex className="min-max" w="100%" justifyContent={'center'} gap="16px">
              <Text color="whiteAlpha.500" _hover={{ color: 'orange.400' }} cursor={'pointer'}>
                MIN
              </Text>
              <Text color="whiteAlpha.500" _hover={{ color: 'orange.400' }} cursor={'pointer'}>
                MAX
              </Text>
            </Flex>
          </Flex>
        </Flex>
        <Button w={{ base: '192px', md: '296px' }} onClick={onBurn} variant="orange">
          Burn
        </Button>
      </Flex>
    </Box>
  )
}

